package br.edu.ifpb.pps;

public class Produto {
	private String nome;
	private float preco;
	
	public Produto() {
		// TODO Auto-generated constructor stub
	}
	public String getNome() {
		return nome;
	}
	public float getPreco() {
		return preco;
	}
	public void setNome(String nome) {
		this.nome = nome;
	}
	public void setPreco(float preco) {
		this.preco = preco;
	}

}
